* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    height: 100%;
    width: 100%;
    line-height: 1.25;
    font-size: 20px;
}

body {
    background: #eee;
}

nav {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    width: 100%;
    height: 56px;
    border-radius: none;
        ul {
            display: flex;
            justify-content: space-evenly;
            align-items: center;
            list-style: none;
                li {
                    padding: 1em;
                    font-size: .85em;

                    .logo {
                        font-size: 1.5em;
                        font-weight: bold;
                    }

                    a {
                        color: #FFF;
                        &:hover {
                            text-decoration: none;
                            color: rgba(255,255,255,.8);
                            font-weight: .85em;
                        }
                    }

                    .get-started-btn {
                        border: 1px solid #fff;
                        color: #fff;
                        font-size: .90em;
                        &:hover {
                            background: #fff;
                            color: #000;
                        }
                    }

                    .badge {
                        padding: .75em;
                    }
                }
        }
}

.jumbotron {
    letter-spacing: 1px;
    border-radius: none;
    height: 70vh;
    width: 100%;
    text-align: center;
    color: #FFF;
    background: #1552F0 !important;
        h1 {
            font-weight: 500;
        }
        p {
            font-weight: 300;
            font-size: .85em
        }
}

.form {
    display: flex;
    justify-content: center;
    align-items: center;
        input {
            height: 60px;
            width: 300px;
            border-radius: 5px; 
            font-size: .85em;
            text-align: left;
            padding-left: 11px;
            outline: none;
            border: none;
        }
        button {
            height: 60px;
            margin: 15px;
            font-size: .85em;
        }
}

.ticker-table {
    margin: -150px auto;
    background: #FFF;
    max-width: 80%;
    font-size: .90em;
    border-radius: 10px;
    border: none;
    outline: none;
        .table-head {
            font-size: .90em;
            color: grey;
            font-weight: 300;
        }
}

.change {
    color: rgb(219, 12, 12);
}

.chart {
    height: 100%;
    width: auto;
}

td {
    height: 40px;
}

.coin-img {
    max-width: 40px;
    margin-right: 15px;
}

section#earn {
    margin-top: 100px;

    .earn-desc {
        button {
            background: #0946E4;
            padding: 10px 17px;
            font-size: .75em;
            &:hover {
                background: rgb(33, 88, 227);
            }
        }
        h2 {
            font-size: 1.5em;
        }
        p {
            font-size: .75em;
        }
    }

    .earn-cryptos {
        // media queries
        .list-group-item {
            font-weight: 600;
            position: relative;
            border: none;
            margin: 3px;
            transition: .5s;
            &:hover {
                box-shadow: 1px 2px 10px #ccc;
                cursor: pointer;
            }
            span {
                margin-left: 10px;
                font-weight: 500;
                font-size: .80em;
                color: #b0b0b0;
            }
            span.earn-amt {
                color: #0d9e0d;
                position: absolute;
                right: 0;
                padding: 9px;
            }
        }
        img {
            max-width: 40px;
            min-width: 25px;
            margin: 0 .5em;
        }
    }
}

#spotlightHero {
    margin-top: 100px;
    min-height: 100vh;
    height: 100%;

        .portfolio-intro {
            text-align: center;
            padding-bottom: 3em;
            p {
                color: #9BA9BF;
                font-size: .85em;
                font-weight: 300;
            }
            h2 {
                color: #0F3156;
            }
        }

        .list-group {
            font-size: .90em;
            li {
                border: none;
                img {
                    height: 75px;
                    width: 75px;
                    border-radius: 50%;
                    box-shadow: 1px 3px 10px #ccc;
                }

            }
            p {
                color: #9BA9BF;
                font-size: .85em;
                letter-spacing: 1px;
            }
        }
        .coinbase-home-img {
            max-width: 100%;
        }
}

#trustHero {
    padding-top: 50px;
    text-align: center;

    h6 {
        padding: 30px 0;
    }

    p {
        color: #9BA9BF;
        font-size: .90em;
        font-weight: 300;
    }

    a {
        font-size: .80em;
    }
    img {
        max-width: 62px;
        margin: 25px;
    }
    .trust-intro {

        h2 {
            color: #0F3156;
        }
        p {
            color: #9BA9BF;
            padding-bottom: 25px;
        }
    }
}

#stats {
    margin-top: 75px;
    padding: 1.5em;
    text-align: center;
    background: #1552F0;
    color: #fff;
    min-height: 200px;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    font-weight: 500;
}

#getStarted {
    text-align: center;
    margin: 50px 0;

    h4 {
        font-size: .95em;
    }

    img {
        z-index: 999;
        max-width: 50px;
        margin: 25px 0;
        padding-bottom: 15px;
        position: relative;
        &:after {
            content: "";
            position: absolute;
            height: 2px;
            width: 100px;
            background: #333;
        }
    }
}

#earnHero {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    min-height: 70vh;
    height: 100%;
    background: url('../assets/cryptonaut.webp');
    background-repeat: no-repeat;
    background-size: cover;
    background-position: right;
    position: relative;
    color: #000;
    &:before {
        content: "";
        position: absolute;
        height: 100%;
        width: 100%;
        background: rgba(51, 51, 51, 0.187);
    }
}

#footer {
    footer {
        min-height: 60vh;
        height: 100%;
        width: 100%;
        background: #fff;
        margin-top: 100px;
        font-size: .85em;
        color: #9BA9BF;
        text-align: center;

        ul {
            list-style: none;
        }

        .footer-logo {
            color: #1552F0;
            font-size: 1.25em;
            margin: .5em 0;
            font-weight: 500;
        }

        .footer-lead {
            margin: 1em 0;
            color: #000;
            font-weight: 500;
        }
    }
}
